/** * ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ * * ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ *      FILE:       MineSweeperApp.java *      DATE:       2/6/2023 *      AUTHOR:     TNHo *      VERSION:    1.0 *      PURPOSE:    Create the game MineSweeper in Java. Our main class. *  * ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ * * ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */// NOTE: THIS MAIN LOOKS MESSYpackage Main;import java.awt.*;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;import javax.swing.*;public class MineSweeperApp {    private static int hei = 9;    private static int wid = 9;    private static int l = 495;    private static int w = l;    private static int mineNum = 12;    private static int tileSize = 55;    private static MineSweeperFrame flp;        public static void main(String[] args) {        JFrame jf = new JFrame("MineSweeper");        JMenuBar mb = new JMenuBar(); // Create new menubar        JMenu x = new JMenu("Menu");        JMenuItem m1 = new JMenuItem("Presets");        JMenuItem m2 = new JMenuItem("Reset");        JMenuItem m3 = new JMenuItem("Custom");        x.add(m1);        x.add(m3);        x.add(m2);        mb.add(x);        jf.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);        jf.setResizable(false);        jf.setSize(510, 555+70);        Container c = jf.getContentPane();        flp = new MineSweeperFrame();        m2.addActionListener(new ActionListener() {            public void actionPerformed(ActionEvent e) {                // Reset                flp.resetti(hei, wid, mineNum, l, w, tileSize);            }        });        m1.addActionListener(new ActionListener() {            public void actionPerformed(ActionEvent e) {                // Open a new window                JDialog settingsDialog = new JDialog(jf, "Presets", true);                JLabel modeSelect = new JLabel("Modes:");                modeSelect.setBounds(20, 20, 100, 25);                modeSelect.setVerticalAlignment(JLabel.TOP);                JButton easyM = new JButton("Easy");                JButton mediM = new JButton("Medium");                JButton hardM = new JButton("Hard");                JButton lunaticM = new JButton("INSANE"); // Basically impossible                easyM.setBounds(10, 50, 80, 25);                mediM.setBounds(90, 50, 80, 25);                hardM.setBounds(170, 50, 80, 25);                lunaticM.setBounds(250, 50, 80, 25);                easyM.addActionListener(new ActionListener() {                    public void actionPerformed(ActionEvent e) {                        System.out.println("Clicked");                        jf.setSize(510, 555+70);                        hei = 9;                        wid = 9;                        l = 495;                        w = l;                        mineNum = 12;                        tileSize = 55;                        flp.resetti(hei, wid, mineNum, l, w, tileSize);                    }                });                mediM.addActionListener(new ActionListener() {                    public void actionPerformed(ActionEvent e) {                        System.out.println("Clicked");                        jf.setSize(538, 583+70);                        hei = 16;                        wid = 16;                        l = 528;                        w = l;                        mineNum = 40;                        tileSize = 33;                        flp.resetti(hei, wid, mineNum, l, w, tileSize);                    }                });                hardM.addActionListener(new ActionListener() {                    public void actionPerformed(ActionEvent e) {                        System.out.println("Clicked");                        jf.setSize(1000, 583+70);                        hei = 30;                        wid = 16;                        l = 990;                        w = 528;                        mineNum = 99;                        tileSize = 33;                        flp.resetti(hei, wid, mineNum, l, w, tileSize);                    }                });                lunaticM.addActionListener(new ActionListener() {                    public void actionPerformed(ActionEvent e) {                        System.out.println("Clicked");                        jf.setSize(1066, 720+70);                        hei = 32;                        wid = 20;                        l = 1056;                        w = 660;                        mineNum = 130;                        tileSize = 33;                        flp.resetti(hei, wid, mineNum, l, w, tileSize);                    }                });                settingsDialog.add(easyM);                settingsDialog.add(mediM);                settingsDialog.add(hardM);                settingsDialog.add(lunaticM);                settingsDialog.add(modeSelect);                settingsDialog.setSize(370, 170);                settingsDialog.setVisible(true);            }        });        m3.addActionListener(new ActionListener() {            public void actionPerformed(ActionEvent e) {                // Open a new window                JDialog settingsDialog = new JDialog(jf, "Custom board:", true);                JPanel panel = new JPanel();                settingsDialog.setContentPane(panel);                //Start                javax.swing.JLabel customLabel = new javax.swing.JLabel();                javax.swing.JLabel jLabel1 = new javax.swing.JLabel();                javax.swing.JTextField custMineNumTF = new javax.swing.JTextField();                javax.swing.JLabel customHeight = new javax.swing.JLabel();                javax.swing.JTextField custHeightTF = new javax.swing.JTextField();                javax.swing.JLabel widthLB = new javax.swing.JLabel();                javax.swing.JTextField custWidthTF = new javax.swing.JTextField();                javax.swing.JButton selectedButton = new javax.swing.JButton();                customLabel.setText("Custom board");                jLabel1.setText("Number of mines:");                customHeight.setText("Height:");                widthLB.setText("Width:");                selectedButton.setText("Ok");                GroupLayout layout = new GroupLayout(panel);                panel.setLayout(layout);                                layout.setHorizontalGroup(                    layout.createParallelGroup(javax.swing.GroupLayout.Alignment.LEADING)                    .addGroup(layout.createSequentialGroup()                        .addContainerGap()                        .addGroup(layout.createParallelGroup(javax.swing.GroupLayout.Alignment.LEADING)                            .addComponent(jLabel1)                            .addComponent(customHeight)                            .addComponent(widthLB))                        .addGap(49, 49, 49)                        .addGroup(layout.createParallelGroup(javax.swing.GroupLayout.Alignment.LEADING)                            .addComponent(selectedButton)                            .addGroup(layout.createParallelGroup(javax.swing.GroupLayout.Alignment.LEADING, false)                                .addComponent(customLabel)                                .addComponent(custMineNumTF)                                .addComponent(custHeightTF)                                .addComponent(custWidthTF, javax.swing.GroupLayout.DEFAULT_SIZE, 131, Short.MAX_VALUE)))                        .addContainerGap(118, Short.MAX_VALUE))                );                layout.setVerticalGroup(                    layout.createParallelGroup(javax.swing.GroupLayout.Alignment.LEADING)                    .addGroup(layout.createSequentialGroup()                        .addContainerGap()                        .addComponent(customLabel)                        .addGap(18, 18, 18)                        .addGroup(layout.createParallelGroup(javax.swing.GroupLayout.Alignment.BASELINE)                            .addComponent(jLabel1)                            .addComponent(custMineNumTF, javax.swing.GroupLayout.PREFERRED_SIZE, javax.swing.GroupLayout.DEFAULT_SIZE, javax.swing.GroupLayout.PREFERRED_SIZE))                        .addGap(18, 18, 18)                        .addGroup(layout.createParallelGroup(javax.swing.GroupLayout.Alignment.BASELINE)                            .addComponent(customHeight)                            .addComponent(custHeightTF, javax.swing.GroupLayout.PREFERRED_SIZE, javax.swing.GroupLayout.DEFAULT_SIZE, javax.swing.GroupLayout.PREFERRED_SIZE))                        .addGap(18, 18, 18)                        .addGroup(layout.createParallelGroup(javax.swing.GroupLayout.Alignment.BASELINE)                            .addComponent(widthLB)                            .addComponent(custWidthTF, javax.swing.GroupLayout.PREFERRED_SIZE, javax.swing.GroupLayout.DEFAULT_SIZE, javax.swing.GroupLayout.PREFERRED_SIZE))                        .addPreferredGap(javax.swing.LayoutStyle.ComponentPlacement.RELATED, 28, Short.MAX_VALUE)                        .addComponent(selectedButton)                        .addGap(27, 27, 27))                );                                 selectedButton.addActionListener(new ActionListener() {                    public void actionPerformed(ActionEvent e) {                        System.out.println("Clicked");                        try {                            if(Integer.parseInt(custHeightTF.getText())<=0||                                Integer.parseInt(custWidthTF.getText())<=0) {                                System.out.println("NO!");                                flp.playSound("sounds/fatalerror.wav");                            } else if(Integer.parseInt(custMineNumTF.getText()) > (Integer.parseInt(custHeightTF.getText())*Integer.parseInt(custWidthTF.getText()))-5                                    || Integer.parseInt(custMineNumTF.getText()) <=0) {                                System.out.println("NO!"); // TO MANY MINES                                flp.playSound("sounds/fatalerror.wav");                            }else {                                wid = Integer.parseInt(custHeightTF.getText());                                hei = Integer.parseInt(custWidthTF.getText());                                tileSize = 33;                                jf.setSize((hei*tileSize)+10, (wid*tileSize)+50+70);                                l = hei*tileSize;                                w = wid*tileSize;                                mineNum = Integer.parseInt(custMineNumTF.getText());                                flp.resetti(hei, wid, mineNum, l, w, tileSize);                            }                        } catch(Exception err) {                            System.out.println(err);                            flp.playSound("sounds/fatalerror.wav");                        }                    }                });                                //End                settingsDialog.add(customHeight);                settingsDialog.add(widthLB);                settingsDialog.add(jLabel1);                settingsDialog.add(custMineNumTF);                settingsDialog.add(custHeightTF);                settingsDialog.add(custWidthTF);                settingsDialog.setSize(400, 300);                settingsDialog.setVisible(true);            }        });        jf.setJMenuBar(mb);        jf.add(flp);        jf.setVisible(true);    }}